import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { API_URL } from "../config";

function TicketList() {
    const [tickets, setTickets] = useState([]);

    useEffect(() => {
        loadTickets();
    }, []);

    const loadTickets = async () => {
        try {
            const response = await axios.get(`${API_URL}/tickets`);
            setTickets(response.data);
        } catch (error) {
            console.error("Error loading tickets:", error);
        }
    };

    const handleStatusUpdate = async (id, newStatus, priorityRating = null) => {
        try {
            const updateData = { status: newStatus };
            if (priorityRating) {
                updateData.priorityRating = priorityRating;
            }
            
            await axios.put(`${API_URL}/ticket/${id}`, updateData);
            loadTickets();
        } catch (error) {
            console.error("Error updating ticket:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`${API_URL}/ticket/${id}`);
            loadTickets();
        } catch (error) {
            console.error("Error deleting ticket:", error);
        }
    };

    return (
        <div className="container mt-5">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Ticket List</h2>
                <Link to="/add" className="btn btn-primary">
                    Create New Ticket
                </Link>
            </div>
            <div className="table-responsive">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Priority</th>
                            <th>Created At</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tickets.map((ticket) => (
                            <tr key={ticket._id}>
                                <td>{ticket.title}</td>
                                <td>{ticket.description}</td>
                                <td>{ticket.status}</td>
                                <td>{ticket.priorityRating || '-'}</td>
                                <td>{new Date(ticket.createdAt).toLocaleString()}</td>
                                <td>
                                    {ticket.status === 'new' && (
                                        <button
                                            className="btn btn-success me-2"
                                            onClick={() => handleStatusUpdate(ticket._id, 'active', '1')}
                                        >
                                            Start Work
                                        </button>
                                    )}
                                    {ticket.status === 'active' && (
                                        <button
                                            className="btn btn-warning me-2"
                                            onClick={() => handleStatusUpdate(ticket._id, 'closed')}
                                        >
                                            Close
                                        </button>
                                    )}
                                    <button
                                        className="btn btn-danger"
                                        onClick={() => handleDelete(ticket._id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default TicketList;
