import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { API_URL } from "../config";
import { useAuth } from '../context/AuthContext';

function TicketList() {
    const [tickets, setTickets] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [selectedTicketId, setSelectedTicketId] = useState(null);
    const [priorityRating, setPriorityRating] = useState("1");
    const { user, logout } = useAuth();

    useEffect(() => {
        loadTickets();
    }, []);

    const loadTickets = async () => {
        try {
            const response = await axios.get(`${API_URL}/tickets`);
            setTickets(response.data);
        } catch (error) {
            console.error("Error loading tickets:", error);
            if (error.response?.status === 401) {
                logout();
            }
        }
    };

    const handleStartWork = (ticketId) => {
        setSelectedTicketId(ticketId);
        setShowModal(true);
    };

    const handleStatusUpdate = async (id, newStatus, priorityRating = null) => {
        try {
            const updateData = { status: newStatus };
            if (priorityRating) {
                updateData.priorityRating = priorityRating;
            }
            
            await axios.put(`${API_URL}/ticket/${id}`, updateData);
            loadTickets();
            setShowModal(false);
        } catch (error) {
            console.error("Error updating ticket:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`${API_URL}/ticket/${id}`);
            loadTickets();
        } catch (error) {
            console.error("Error deleting ticket:", error);
        }
    };

    const canDeleteTicket = (ticket) => {
        return user.status === 'admin';
    };

    const canUpdateTicket = (ticket) => {
        return user.status === 'admin' || 
               user.status === 'support' || 
               (user.status === 'user' && ticket.userId === user._id);
    };

    return (
        <div className="container mt-5">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Ticket List</h2>
                <div>
                    <Link to="/add" className="btn btn-primary me-2">
                        Create New Ticket
                    </Link>
                    <button onClick={logout} className="btn btn-outline-danger">
                        Logout
                    </button>
                </div>
            </div>
            <div className="table-responsive">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Priority</th>
                            <th>Created At</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tickets.map((ticket) => (
                            <tr key={ticket._id}>
                                <td>{ticket.title}</td>
                                <td>{ticket.description}</td>
                                <td>{ticket.status}</td>
                                <td>{ticket.priorityRating || '-'}</td>
                                <td>{new Date(ticket.createdAt).toLocaleString()}</td>
                                <td>
                                    {canUpdateTicket(ticket) && ticket.status === 'new' && (
                                        <button
                                            className="btn btn-success me-2"
                                            onClick={() => handleStartWork(ticket._id)}
                                        >
                                            Start Work
                                        </button>
                                    )}
                                    {canUpdateTicket(ticket) && ticket.status === 'active' && (
                                        <button
                                            className="btn btn-warning me-2"
                                            onClick={() => handleStatusUpdate(ticket._id, 'closed')}
                                        >
                                            Close
                                        </button>
                                    )}
                                    {canDeleteTicket(ticket) && (
                                        <button
                                            className="btn btn-danger"
                                            onClick={() => handleDelete(ticket._id)}
                                        >
                                            Delete
                                        </button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Priority Rating Modal */}
            {showModal && (
                <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Set Priority Rating</h5>
                                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <div className="mb-3">
                                    <label className="form-label">Select Priority Rating (1-4)</label>
                                    <select 
                                        className="form-select"
                                        value={priorityRating}
                                        onChange={(e) => setPriorityRating(e.target.value)}
                                    >
                                        <option value="1">1 - Lowest</option>
                                        <option value="2">2 - Low</option>
                                        <option value="3">3 - High</option>
                                        <option value="4">4 - Highest</option>
                                    </select>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button 
                                    type="button" 
                                    className="btn btn-secondary" 
                                    onClick={() => setShowModal(false)}
                                >
                                    Cancel
                                </button>
                                <button 
                                    type="button" 
                                    className="btn btn-primary"
                                    onClick={() => handleStatusUpdate(selectedTicketId, 'active', priorityRating)}
                                >
                                    Confirm
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default TicketList;
