import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import AddTicket from './addticket/AddTicket';
import UserTicketList from './ticketlist/UserTicketList';
import SupportTicketList from './ticketlist/SupportTicketList';
import AdminTicketList from './ticketlist/AdminTicketList';
import Header from './components/Header';

function ProtectedRoute({ children }) {
    const { user } = useAuth();
    if (!user) {
        return <Navigate to="/login" />;
    }
    return children;
}

function TicketListWrapper() {
    const { user } = useAuth();

    switch (user.status) {
        case 'admin':
            return <AdminTicketList />;
        case 'support':
            return <SupportTicketList />;
        case 'user':
            return <UserTicketList />;
        default:
            return <UserTicketList />;
    }
}

function AppContent() {
    const { user } = useAuth();

    return (
        <Router>
            {user && <Header />}
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route
                    path="/tickets"
                    element={
                        <ProtectedRoute>
                            {user?.status === 'admin' ? <AdminTicketList /> : <SupportTicketList />}
                        </ProtectedRoute>
                    }
                />
                <Route
                    path="/add"
                    element={
                        <ProtectedRoute>
                            <AddTicket />
                        </ProtectedRoute>
                    }
                />
                <Route path="/" element={<Navigate to="/tickets" />} />
            </Routes>
        </Router>
    );
}

function App() {
    return (
        <AuthProvider>
            <AppContent />
        </AuthProvider>
    );
}

export default App;
