import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_URL } from '../config';
import { useAuth } from '../context/AuthContext';

function KnowledgeBase() {
    const [articles, setArticles] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [editingArticle, setEditingArticle] = useState(null);
    const [formData, setFormData] = useState({ title: '', body: '' });
    const { user } = useAuth();

    const canEdit = user.status === 'support' || user.status === 'admin';

    useEffect(() => {
        loadArticles();
    }, []);

    const loadArticles = async () => {
        try {
            const response = await axios.get(`${API_URL}/knowledge-base`);
            setArticles(response.data);
        } catch (error) {
            console.error('Error loading articles:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            console.log('Submitting form data:', formData);
            
            if (editingArticle) {
                const response = await axios.put(`${API_URL}/knowledge-base/${editingArticle._id}`, formData);
                console.log('Update response:', response.data);
            } else {
                const response = await axios.post(`${API_URL}/knowledge-base`, formData);
                console.log('Create response:', response.data);
            }
            
            setShowModal(false);
            setEditingArticle(null);
            setFormData({ title: '', body: '' });
            await loadArticles();
        } catch (error) {
            console.error('Error saving article:', error.response?.data || error.message);
            alert('Ошибка при сохранении статьи: ' + (error.response?.data?.message || error.message));
        }
    };

    const handleEdit = (article) => {
        setEditingArticle(article);
        setFormData({ title: article.title, body: article.body });
        setShowModal(true);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Вы уверены, что хотите удалить эту статью?')) {
            try {
                await axios.delete(`${API_URL}/knowledge-base/${id}`);
                loadArticles();
            } catch (error) {
                console.error('Error deleting article:', error);
            }
        }
    };

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>База знаний</h2>
                {canEdit && (
                    <button 
                        className="btn btn-primary"
                        onClick={() => {
                            setEditingArticle(null);
                            setFormData({ title: '', body: '' });
                            setShowModal(true);
                        }}
                    >
                        Создать статью
                    </button>
                )}
            </div>

            <div className="row">
                {articles.map((article) => (
                    <div key={article._id} className="col-md-6 mb-4">
                        <div className="card h-100">
                            <div className="card-body">
                                <h5 className="card-title">{article.title}</h5>
                                <p className="card-text">{article.body}</p>
                                <p className="card-text">
                                    <small className="text-muted">
                                        Автор: {article.userId?.username}
                                    </small>
                                </p>
                                {canEdit && (
                                    <div className="mt-3">
                                        <button
                                            className="btn btn-sm btn-primary me-2"
                                            onClick={() => handleEdit(article)}
                                        >
                                            Редактировать
                                        </button>
                                        <button
                                            className="btn btn-sm btn-danger"
                                            onClick={() => handleDelete(article._id)}
                                        >
                                            Удалить
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {showModal && (
                <div className="modal" style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}>
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">
                                    {editingArticle ? 'Редактировать статью' : 'Создать статью'}
                                </h5>
                                <button
                                    type="button"
                                    className="btn-close"
                                    onClick={() => {
                                        setShowModal(false);
                                        setEditingArticle(null);
                                        setFormData({ title: '', body: '' });
                                    }}
                                ></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="mb-3">
                                        <label className="form-label">Заголовок</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={formData.title}
                                            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                            required
                                        />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Содержание</label>
                                        <textarea
                                            className="form-control"
                                            rows="5"
                                            value={formData.body}
                                            onChange={(e) => setFormData({ ...formData, body: e.target.value })}
                                            required
                                        ></textarea>
                                    </div>
                                    <div className="modal-footer">
                                        <button
                                            type="button"
                                            className="btn btn-secondary"
                                            onClick={() => {
                                                setShowModal(false);
                                                setEditingArticle(null);
                                                setFormData({ title: '', body: '' });
                                            }}
                                        >
                                            Отмена
                                        </button>
                                        <button type="submit" className="btn btn-primary">
                                            {editingArticle ? 'Сохранить' : 'Создать'}
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default KnowledgeBase;
