import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { API_URL } from "../config";
import { useAuth } from '../context/AuthContext';

function UserTicketList() {
    const [tickets, setTickets] = useState([]);
    const { user, logout } = useAuth();

    const loadTickets = async () => {
        try {
            console.log('Loading tickets for user:', user);
            const response = await axios.get(`${API_URL}/tickets`, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            console.log('Received tickets:', response.data);
            // Фильтруем тикеты только для текущего пользователя
            const userTickets = response.data.filter(ticket => ticket.userId._id === user._id);
            console.log('Filtered user tickets:', userTickets);
            setTickets(userTickets);
        } catch (error) {
            console.error("Error loading tickets:", error);
            if (error.response?.status === 401) {
                logout();
            }
        }
    };

    useEffect(() => {
        loadTickets();
    }, [user._id]); // Добавляем user._id в зависимости

    return (
        <div className="container mt-5">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <h2>Мои тикеты</h2>
                <div>
                    <Link to="/add" className="btn btn-primary me-2">
                        Создать тикет
                    </Link>
                </div>
            </div>
            <div className="table-responsive">
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Название</th>
                            <th>Описание</th>
                            <th>Статус</th>
                            <th>Приоритет</th>
                            <th>Дата создания</th>
                            <th>Автор</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tickets.map((ticket) => (
                            <tr key={ticket._id}>
                                <td>{ticket.title}</td>
                                <td>{ticket.description}</td>
                                <td>{ticket.status}</td>
                                <td>{ticket.priorityRating || '-'}</td>
                                <td>{new Date(ticket.createdAt).toLocaleString()}</td>
                                <td>{ticket.userId?.username || '-'}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default UserTicketList;
