import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_URL } from '../config';

function UserKnowledgeBase() {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        const loadArticles = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${API_URL}/bazaznaniy`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setArticles(response.data);
            } catch (error) {
                console.error('Ошибка при загрузке статей:', error.response?.data || error.message);
            }
        };
        loadArticles();
    }, []);

    return (
        <div className="container mt-5">
            <h2>База знаний</h2>
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">Полезные материалы</h5>
                    <p className="card-text">Здесь вы найдете полезные статьи и инструкции для решения распространенных проблем.</p>
                    <div className="list-group mt-4">
                        {articles.map((article) => (
                            <div key={article._id} className="list-group-item list-group-item-action">
                                <h5 className="mb-1">{article.title}</h5>
                                <p className="mb-1" style={{ whiteSpace: 'pre-wrap' }}>{article.body}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default UserKnowledgeBase;
