import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import AddTicket from './components/AddTicket';
import UserTicketList from './ticketlist/UserTicketList';
import SupportTicketList from './ticketlist/SupportTicketList';
import AdminTicketList from './ticketlist/AdminTicketList';

function ProtectedRoute({ children }) {
    const { user } = useAuth();
    if (!user) {
        return <Navigate to="/login" />;
    }
    return children;
}

function TicketListWrapper() {
    const { user } = useAuth();

    switch (user.status) {
        case 'admin':
            return <AdminTicketList />;
        case 'support':
            return <SupportTicketList />;
        case 'user':
            return <UserTicketList />;
        default:
            return <UserTicketList />;
    }
}

function App() {
    return (
        <AuthProvider>
            <Router>
                <Routes>
                    <Route path="/login" element={<Login />} />
                    <Route path="/register" element={<Register />} />
                    <Route
                        path="/"
                        element={
                            <ProtectedRoute>
                                <TicketListWrapper />
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/add"
                        element={
                            <ProtectedRoute>
                                <AddTicket />
                            </ProtectedRoute>
                        }
                    />
                </Routes>
            </Router>
        </AuthProvider>
    );
}

export default App;
