import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_URL } from '../config';

function UserKnowledgeBase() {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        const loadArticles = async () => {
            try {
                const response = await axios.get(`${API_URL}/bazaznaniy`);
                setArticles(response.data);
            } catch (error) {
                console.error('Ошибка при загрузке статей:', error);
            }
        };
        loadArticles();
    }, []);

    return (
        <div className="container mt-5">
            <h2>База знаний</h2>
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">Полезные материалы</h5>
                    <p className="card-text">Здесь вы найдете полезные статьи и инструкции для решения распространенных проблем.</p>
                    <div className="list-group mt-4">
                        {articles.map((article) => (
                            <div key={article._id} className="list-group-item list-group-item-action">
                                <div className="d-flex w-100 justify-content-between">
                                    <h5 className="mb-1">{article.title}</h5>
                                    <small>{new Date(article.createdAt).toLocaleDateString()}</small>
                                </div>
                                <p className="mb-1">{article.body}</p>
                                <small>Автор: {article.userId?.username || 'Неизвестно'}</small>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default UserKnowledgeBase;
