import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import Login from './components/Login';
import Register from './components/Register';
import AddTicket from './addticket/AddTicket';
import UserTicketList from './ticketlist/UserTicketList';
import SupportTicketList from './ticketlist/SupportTicketList';
import AdminTicketList from './ticketlist/AdminTicketList';
import Header from './components/Header';
import KnowledgeBase from './knowledgebase/KnowledgeBase';

function ProtectedRoute({ children }) {
    const { user } = useAuth();
    if (!user) {
        return <Navigate to="/login" />;
    }
    return children;
}

function TicketListWrapper() {
    const { user } = useAuth();

    switch (user.status) {
        case 'admin':
            return <AdminTicketList />;
        case 'support':
            return <SupportTicketList />;
        case 'user':
            return <UserTicketList />;
        default:
            return <UserTicketList />;
    }
}

function App() {
    return (
        <AuthProvider>
            <Router>
                <div className="d-flex flex-column min-vh-100">
                    <Header />
                    <main className="flex-grow-1">
                        <Routes>
                            <Route path="/login" element={<Login />} />
                            <Route path="/register" element={<Register />} />
                            <Route
                                path="/"
                                element={
                                    <ProtectedRoute>
                                        <TicketListWrapper />
                                    </ProtectedRoute>
                                }
                            />
                            <Route
                                path="/add"
                                element={
                                    <ProtectedRoute>
                                        <AddTicket />
                                    </ProtectedRoute>
                                }
                            />
                            <Route
                                path="/knowledge-base"
                                element={
                                    <ProtectedRoute>
                                        <KnowledgeBase />
                                    </ProtectedRoute>
                                }
                            />
                        </Routes>
                    </main>
                </div>
            </Router>
        </AuthProvider>
    );
}

export default App;
