import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import './UserTicketList.css';

const UserTicketList = () => {
    const [tickets, setTickets] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const { logout } = useAuth();

    useEffect(() => {
        loadTickets();
    }, []);

    const loadTickets = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('No authentication token found');
            }

            const response = await axios.get('http://localhost:5000/api/tickets', {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            console.log('Tickets loaded:', response.data);
            setTickets(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Error loading tickets:', error);
            setError('Ошибка при загрузке тикетов');
            if (error.response && error.response.status === 401) {
                logout();
            }
            setLoading(false);
        }
    };

    if (loading) return <div>Загрузка...</div>;
    if (error) return <div className="error-message">{error}</div>;

    return (
        <div className="ticket-list-container">
            <h2>Мои тикеты</h2>
            {tickets.length === 0 ? (
                <p>У вас пока нет тикетов</p>
            ) : (
                <div className="tickets-grid">
                    {tickets.map((ticket) => (
                        <div key={ticket._id} className="ticket-card">
                            <h3 className="ticket-title">{ticket.title}</h3>
                            <p className="ticket-description">{ticket.description}</p>
                            <div className="ticket-details">
                                <span className={`status status-${ticket.status}`}>
                                    {ticket.status === 'new' ? 'Новый' :
                                     ticket.status === 'active' ? 'В работе' : 'Закрыт'}
                                </span>
                                {ticket.priorityRating && (
                                    <span className="priority">
                                        Приоритет: {ticket.priorityRating}
                                    </span>
                                )}
                                <span className="date">
                                    Создан: {new Date(ticket.createdAt).toLocaleDateString()}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default UserTicketList;
