import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_URL } from '../config';
import { useAuth } from '../context/AuthContext';

function SLA() {
    const [stats, setStats] = useState({
        totalTickets: 0,
        activeTickets: 0,
        closedTickets: 0,
        closedPercentage: 0,
        avgResponseTime: 0,
        avgResolutionTime: 0
    });
    const { logout } = useAuth();

    useEffect(() => {
        loadStats();
    }, []);

    const loadStats = async () => {
        try {
            const response = await axios.get(`${API_URL}/tickets/stats`, {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            setStats(response.data);
        } catch (error) {
            console.error("Error loading stats:", error);
            if (error.response?.status === 401) {
                logout();
            }
        }
    };

    const formatTime = (minutes) => {
        const hours = Math.floor(minutes / 60);
        const mins = Math.round(minutes % 60);
        return `${hours}ч ${mins}м`;
    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">SLA Статистика</h2>
            <div className="row">
                <div className="col-md-4 mb-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Общая статистика</h5>
                            <p className="card-text">Всего тикетов: {stats.totalTickets}</p>
                            <p className="card-text">Активных тикетов: {stats.activeTickets}</p>
                            <p className="card-text">Закрытых тикетов: {stats.closedTickets}</p>
                            <p className="card-text">Процент закрытых: {stats.closedPercentage.toFixed(1)}%</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4 mb-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Время реакции</h5>
                            <p className="card-text">
                                Среднее время реакции: {formatTime(stats.avgResponseTime)}
                            </p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4 mb-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Время решения</h5>
                            <p className="card-text">
                                Среднее время решения: {formatTime(stats.avgResolutionTime)}
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default SLA;
